import React, {useEffect} from 'react';
import {Link, useParams} from "react-router-dom";
import MainHeader from "../../../layout/components/main-Header/MainHeader";
import * as contactFeature from '../../../../redux/contacts/contacts.slice';
import * as contactActions from '../../../../redux/contacts/contacts.actions';
import {useDispatch, useSelector} from "react-redux";
import {AppDispatch} from "../../../../redux/store";
import LoadingSpinner from "../../../util/components/spinner/LoadingSpinner";
import ErrorMessage from "../../../util/components/error-message/ErrorMessage";

interface IState {
}

let ViewContact: React.FC = () => {
    let {contactId} = useParams();
    const dispatch: AppDispatch = useDispatch();

    // get the store data from redux
    let contactState = useSelector((store: contactFeature.RootContactState) => {
        return store[contactFeature.contactsFeatureKey];
    });

    const {loading, errorMessage, contact, group} = contactState;

    useEffect(() => {
        if (contactId) {
            dispatch(contactActions.getContactAction(contactId)).then(() => {
                if (Object.keys(contact).length > 0) {
                    dispatch(contactActions.getGroupAction(contact));
                }
            });
        }

    }, [contactId])

    return (
        <>
            <MainHeader heading={'View Contact'}/>

            {/* Loading LoadingSpinner */}
            {
                loading && Object.keys(contact).length === 0 &&
                <LoadingSpinner/>
            }

            {/* Error Message */}
            {
                !loading && errorMessage.message && errorMessage.message?.length > 0 &&
                <ErrorMessage message={errorMessage.message}/>
            }

            {
                !loading && Object.keys(contact).length > 0 &&
                <div className="container mt-3">
                    <div className="row align-items-center">
                        <div className="col-sm-4 text-center">
                            <img src={contact.imageUrl} alt="" className="contact-img-lg img-thumbnail rounded-circle"/>
                        </div>
                        <div className="col-sm-8">
                            <ul className="list-group">
                                <li className="list-group-item list-group-item-action">
                                    Name : <span className="fw-bold">{contact.name}</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Mobile : <span
                                    className="fw-bold">{contact.mobile}</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Email : <span className="fw-bold">{contact.email}</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Company : <span className="fw-bold">{contact.company}</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Title : <span
                                    className="fw-bold">{contact.title}</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Group Name : <span className="fw-bold">{group.name}</span>
                                </li>
                            </ul>
                        </div>
                    </div>
                    <div className="row mt-3">
                        <div className="col">
                            <Link to={'/contacts/admin'} className="btn btn-dark">
                                <i className="fa fa-arrow-circle-left"></i> Back</Link>
                        </div>
                    </div>
                </div>
            }
        </>
    )
};
export default ViewContact;
