import { createSlice } from "@reduxjs/toolkit";
import { IEmployee } from "../../models/IEmployee";
import EmployeeService from "../../services/EmployeeService";

export const employeeFeature = "employeeFeature" ;

export interface InitialState{
    employee:IEmployee[];
}

const initialState:InitialState={
    employee:EmployeeService.getAllEmployee()
}

export const employeeSlice = createSlice({
    name:"Employee Slice",
    initialState:initialState,
    reducers:{
        dispalyRecords:(state,action)=>{
            let {id} = action.payload;
            console.log(id);
            state.employee = state.employee.map((emp)=>{
                if(emp.id === id){
                    return{
                        ...emp,
                        isSelected:!emp.isSelected
                    }
                }
                return emp;
            })
        }
    }
})

export const {dispalyRecords} = employeeSlice.actions;
