import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Navbar from "../../../Layouts/Navbar";
import Spinner from "../../../Layouts/Spinner";
import { IContacts } from "../Models/IContacts";
import ContactService from "../Services/ContactService";


interface IProps{}
interface IState{
    conatct:IContacts[];
}

let Contacts:React.FC<IProps> =() =>{


    let [conatct,setConatct]=useState<IContacts[]>([] as IContacts[]);
    let [loading, setLoading] = useState<boolean>(false);
    let [errorMessage, setErrorMessage] = useState<string>("");
    useEffect(()=>{
        setLoading(true);
        ContactService.getAllContacts().then((response)=>{
            setConatct(()=>response.data)
            setLoading(false);
        }).catch((err)=>{
            console.log(err);
            setErrorMessage(()=>err.message);
            setLoading(false);
        })
    },[])


    return(
        <>
            <Navbar/>
            <div className="grid">
                <div className="container">
                    <div className="row mt-2">
                        <div className="col">
                        <h3>Contact Details</h3>
                        <p>Lorem ipsum dolor sit amet consectetur, adipisicing elit. Sequi quibusdam iusto voluptas, facere alias eos praesentium dicta magnam vero repudiandae ea voluptatem iure consequuntur recusandae labore officiis saepe, reprehenderit repellat!</p>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-4">
                        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
                        </div>
                        <div className="col-sm-3">
                            <button className="btn btn-outline-success" type="submit">Search</button>
                        </div>
                    </div>
                    {
                        loading && <Spinner/>
                    }
                    {
                        !loading && errorMessage &&
                        <div className="container mt-5 display-6">
                            <p className="text-danger text-center">{errorMessage}</p>
                        </div>
                    }
                    {
                        !loading && conatct && conatct.length>0 &&
                        <div className="row mt-3">
                        {
                            conatct.map((contacts)=>{
                                return(
                                    <div className="col-sm-6" key={contacts.id}>
                                        <div className="card shadow-lg mt-2">
                                            <div className="card-body">
                                                <div className="row">
                                                    <div className="col-sm-3">
                                                        <img width={120} src={contacts.imageUrl} alt="" />
                                                    </div>
                                                    <div className="col-sm-6">
                                                        <ul className="list-group">
                                                            <li className="list-group-item">
                                                            Name : {contacts.name}
                                                            </li>
                                                            <li className="list-group-item">
                                                                Email : {contacts.email}
                                                            </li>
                                                            <li className="list-group-item">
                                                                Mobile : {contacts.mobile}
                                                            </li>
                                                        </ul>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>

                                )
                            })
                        }
                        </div>   
                    }
                </div>
            </div>
        </>
    );

}

export default Contacts;
