import React from 'react';
import {useDispatch, useSelector} from "react-redux";
import * as counterReducer from '../redux/counter/counter.feature';
import {counterFeature} from '../redux/counter/counter.feature';
import {AppDispatch} from "../redux/store";

interface CounterState {
    [counterReducer.counterFeature]: counterReducer.InitialState
}

let CounterRedux: React.FC = () => {

    let dispatch: AppDispatch = useDispatch();

    // get the state from Redux store
    let counterState = useSelector((store: CounterState) => {
        return store[counterFeature];
    })

    let {count} = counterState;

    let clickIncrButton = (): void => {
        dispatch({
            type: `${counterReducer.clickIncr}`,
            payload: {}
        });
    };

    let clickDecrButton = (): void => {
        dispatch({
            type: `${counterReducer.clickDecr}`,
            payload: {}
        });
    };

    let clickIncrByButton = (value: number): void => {
        dispatch({
            type: `${counterReducer.clickIncrBy}`,
            payload: {
                value: value
            }
        });
    };

    return (
        <React.Fragment>
            <div className="container mt-5">
                <div className="row">
                    <div className="col-md-4">
                        <div className="card">
                            <div className="card-body">
                                <p className="h3 display-3">Redux State Changing</p>
                                <p className="h3 display-3">{count}</p>
                                <button onClick={clickIncrButton} className="btn btn-success m-1">Increment</button>
                                <button onClick={clickDecrButton} className="btn btn-warning m-1">Decrement</button>
                                <button onClick={() => clickIncrByButton(5)} className="btn btn-danger m-1">Increment by
                                    5
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </React.Fragment>
    )
};
export default CounterRedux;
