import { createAsyncThunk, createSlice, SerializedError } from "@reduxjs/toolkit"
import axios from "axios";
import {IProducts} from "../../Components/Products/Models/IProducts"
import ProductsService from "../../Components/Products/Services/ProductsService"


// async action
export const getAllProductsFromServer = createAsyncThunk("getAllProductFromServer",async():Promise<any>=>{
    const serverUrl : string | undefined = process.env.REACT_APP_JSON_SERVER_URL;
    const response = await axios.get(`${serverUrl}/productssadsdsd`);
    return response.data;
})

export const productFeature = "productFeature";

export interface InitialState{
    product:IProducts[];
    loading:boolean;
    errorMessage:SerializedError;
}

const initialState:InitialState={
    loading:false,
    product: [] as IProducts[],
    errorMessage:{} as SerializedError
}

export const productSlice = createSlice({
    name:"product Slice",
    initialState:initialState,
    reducers:{},
    extraReducers:(builder)=>{
        builder.addCase(getAllProductsFromServer.pending,(state,action)=>{
            state.loading=true;
        }).addCase(getAllProductsFromServer.fulfilled,(state,action)=>{
            state.product=action.payload;
            state.loading=false;
        }).addCase(getAllProductsFromServer.rejected, (state,action)=>{
            state.loading=false;
            state.errorMessage=action.error;
        })
    }
})
