import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Spinner from "../../Layout/Component/Spinner";
import { IUsers , UserResults} from "../Models/IUSers";
import { UserService } from "../Services/UserService";

interface IProps{}

interface IState{}

let UserManager:React.FC<IProps>=()=>{


    let [users, setUsers] = useState<IUsers[]>([] as IUsers[]);
    let [loading, setLoading] = useState(false);
    let [errorMessage, setErrorMessage] = useState("");
    useEffect(()=>{
        setLoading(()=>true);
        UserService.getAllUsers().then((response)=>{
            let UserResults:UserResults= response.data;
            setLoading(()=>false);
            setUsers((prevState)=>{
                return UserResults.results;
            })
        }).catch((err)=>{
            setLoading(()=>false);
            setErrorMessage(()=>err.message);
        });
    },[])

    return(
        <>
        <div className="grid">
            <div className="container">
                <div className="row">
                    <div className="col">
                        <h3 className="text-success">Users List</h3>
                        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Explicabo aspernatur, rerum quibusdam unde distinctio nulla odio atque voluptate voluptatum quas delectus porro tempore beatae repellendus labore eius, itaque harum temporibus!</p>
                    </div>
                </div>
                {
                    loading && <Spinner/>
                }
                {
                    !loading && errorMessage &&
                    <div className="alert alert-danger">
                        <p>{errorMessage}</p>
                    </div>
                }
                {
                    !loading && users && users.length > 0 &&
                    <div className="row">
                        <div className="col">
                            <table className="table table-hover table-light">
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Name</th>
                                        <th>Email</th>
                                        <th>Phone</th>
                                        <th>Address</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        users.map((user)=>{
                                            return(
                                                <tr key={user.login.uuid}>
                                                    <td>{user.login.uuid.substring(user.login.uuid.length-5)}</td>
                                                    <td>
                                                        <Link to={`/users/${user.login.uuid}`}>
                                                            {user.name.title}. {user.name.first} {user.name.last} 
                                                        </Link>
                                                    </td>    
                                                    <td>{user.email}</td>
                                                    <td>{user.phone}</td>
                                                    <td>#{user.location.street.number} {user.location.street.name}<br/>
                                                    {user.location.city}<br/>
                                                    {user.location.country}<br/>
                                                    {user.location.postcode}<br/>
                                                    </td>
                                                </tr>
                                            )
                                        })
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                }

            </div>
        </div>
        </>
    )
}

export default UserManager;
