import React, { useEffect, useState } from "react";
import { IEmployee } from "../models/IEmployee";
import EmployeeService from "../services/EmployeeService";


interface IProps{}
interface IState{
    employees:IEmployee[];
}

let EmployeeRedux:React.FC<IProps> =() =>{

    let [state, setState] = useState<IState>({
        employees:EmployeeService.getAllEmployee()
    });

    let {employees} = state;
    let dispalyRecords=(id:number)=>{
        let selectedEmployee = employees.map((emp)=>{
            if(emp.id === id){
                return{
                    ...emp,
                    isSelected:!emp.isSelected
                }
            }
            return emp;
        });

        setState({
            ...state, employees:selectedEmployee
        })
    }
    return(
        <>
        <div className="grid">
            <div className="container">
                <div className="row mt-5">
                    <div className="col-sm-6">
                        <ul className="list-group">
                            {
                                employees.map((employee)=>{
                                    return(
                                        <li className="list-group-item" key={employee.id}>
                                            <input onChange={()=>dispalyRecords(employee.id)} type="checkbox" className="form-check-input me-2" />
                                            {employee.name}
                                        </li>
                                    )
                                })
                            }
                        </ul>
                    </div>
                    <div className="col-sm-6">
                        {
                            employees.map((emp)=>{
                                return(
                                    <div key={emp.id}>
                                        {
                                            emp.isSelected &&
                                            <div className="card mt-2 shadow-lg">
                                                <div className="card-body">
                                                    <ul className="list-group">
                                                        <li className="list-group-item">
                                                            {emp.name}
                                                        </li>
                                                        <li className="list-group-item">
                                                            {emp.email}
                                                        </li>
                                                    </ul>
                                                </div>
                                            </div>
                                        }
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </div>
        </>
    );

}

export default EmployeeRedux;
