import axios from "axios";
import { IUsers } from "../Models/IUSers";

export class UserService{
    private static serviceUrl:string|undefined = process.env.REACT_APP_USER_API_URL;

    public static getAllUsers(){
        return axios.get(`${this.serviceUrl}`);
    }

    public static getUser(userId:string|undefined){
        return new Promise<any>((resolve, reject) => {
            axios.get(`${this.serviceUrl}`).then((response)=>{
                let result = response.data;
                let users:IUsers[]|undefined= result.results;
                if(users){
                    let user:IUsers | undefined = users.find(user=>user.login.uuid===userId);
                    if(user){
                        resolve(user);
                    }
                }
            }).catch((err)=>{
                reject(err);
            });
        })


    }
}
