import React, { useEffect, useState } from "react";
import Spinner from "../../Layout/Component/Spinner";
import { IProducts } from "../Models/IProducts";
import { ProductService } from "../Services/ProductService";

interface IProps{}

interface IState{}

let ProductList:React.FC<IProps>=()=>{

    let [products, setProducts] = useState<IProducts[]>([] as IProducts[]);
    let [errMessage, setErrMessage] = useState<string>("");
    let [loading,setLoading] = useState<boolean>(false);

    useEffect(()=>{
        ProductService.getAllProducts().then((response)=>{
            setLoading(true);
            setProducts(()=>response.data);
            setLoading(false);
        }).catch((err)=>{
            setErrMessage(()=>err.message);
            setLoading(false);
        })
    },[])

    return(
        <>
        <div className="grid">
            <div className="container">
                <div className="row m-1">
                    <h3>Product Details</h3>
                    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Laudantium molestiae pariatur ut autem, quo veritatis! Voluptate accusantium ratione magni iusto eum sit libero et tempora sapiente earum, nobis optio itaque.</p>
                    {
                        loading && <Spinner/>
                    }
                    {
                        !loading && errMessage &&
                        <div className="row">
                            <div className="col">
                                <p className="h5 text-bg-danger">{errMessage}</p>
                            </div>
                        </div>
                    }
                    <div className="col">
                        {
                            products.map((product)=>{
                                return(
                                    <div className="card mt-1 shadow-lg">
                                        <div className="card-body">
                                            <div className="row" key={product.sno}>
                                                <div className="col-sm-4">
                                                    <img width="80" src={product.image} alt="" />
                                                </div>
                                                <div className="col-sm-3">
                                                    <p>{product.title}</p>
                                                    {
                                                        product.qty >= 10 && 
                                                        <p className="text-success">Available</p>
                                                    }
                                                    {
                                                        product.qty < 10 && product.qty >= 5 &&
                                                        <p className="text-warning">Runnig Out</p>
                                                    }
                                                    {
                                                        product.qty < 5 &&
                                                        <p className="text-danger">Out Of Stock</p>
                                                    }
                                                    <p>&#8377; {product.price.toFixed(2)}</p>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </div>
        </>
    )
}

export default ProductList;
