import React, { useEffect, useState } from "react";
import { IProducts } from "../Models/IProducts";
import { ProductService } from "../Services/ProductService";

interface IProps{}

interface IState{}

let CartList:React.FC<IProps>=()=>{

    let [products, setProducts] = useState<IProducts[]>([] as IProducts[]);
    let [errMessage, setErrMessage] = useState<string>("");

    useEffect(()=>{
        ProductService.getAllProducts().then((response)=>{
            setProducts(()=>response.data);
        }).catch((err)=>{
            setErrMessage(()=>err.message);
        })
    },[])

    let totalPrice = () =>{
        let totalPrice:number=0;
        for(let product of products){
            totalPrice+=product.price*product.qty;
        }
        return totalPrice;
    }

    let deleteProduct=(sno:string)=>{
        setProducts((prevState)=>   
            products.filter((product)=>product.sno !== sno)
        )
    }

    return(
        <>
        <div className="grid">
            <div className="container">
                <div className="ro">
                    <div className="col">
                        <h3>Product Cart</h3>
                        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Iure saepe assumenda ea eum similique quo numquam officiis consequatur. Eum officia expedita porro, quos illo blanditiis nemo voluptatum quod placeat facere?</p>
                    </div>
                    <div className="row">
                        <div className="col">
                            <table className="table table-hover table-light">
                                <thead>
                                    <tr>
                                    <th>S.NO</th>
                                    <th>Image</th>
                                    <th>Quantity</th>
                                    <th>Price</th>
                                    <th>Total</th>
                                    <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        products.map((product)=>{
                                            return(
                                                <tr key={product.sno}>
                                                    <td>{product.sno}</td>
                                                    <td><img width="60" src={product.image} alt="" /></td>
                                                    <td>
                                                        <i className="fa fa-plus-circle"></i>
                                                         &nbsp;{product.qty}

                                                    </td>
                                                    <td>{product.price}</td>
                                                    <td>{product.qty*product.price}</td>
                                                    <td><i onClick={()=>deleteProduct(product.sno)} className="fa fa-trash"></i></td>
                                                </tr>
                                            )
                                        })
                                    }
                                    <tr>
                                        <td colSpan={4}></td>
                                        <td>Total Price </td>
                                        <td>{totalPrice()}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        </>
    )
}

export default CartList;
