import {IUser} from "../../models/IUser";
import {createAsyncThunk, createSlice, SerializedError} from "@reduxjs/toolkit";
import axios from "axios";

// async action
export const getAllUsersFromServer = createAsyncThunk("getAllUsersFromServer", async (): Promise<any> => {
    const serverUrl: string | undefined = process.env.REACT_APP_USERS_SERVER_URL;
    const response = await axios.get(`${serverUrl}/users`);
    return response.data;
})

// feature key
export const userListFeature = "usersFeature";

// Initial State Type
export interface InitialState {
    loading: boolean;
    users: IUser[];
    errorMessage: SerializedError;
}

// Initial State
const initialState = {
    loading: false,
    users: [] as IUser[],
    errorMessage: {} as SerializedError
}

// slice creation
export const userSlice = createSlice({
    name: 'userSlice',
    initialState: initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(getAllUsersFromServer.pending, (state, action) => {
            state.loading = true;
        }).addCase(getAllUsersFromServer.fulfilled, (state, action) => {
            state.loading = false;
            state.users = action.payload;
        }).addCase(getAllUsersFromServer.rejected, (state, action) => {
            state.loading = false;
            state.errorMessage = action.error
        })
    }
})
