import React, {useEffect, useState} from 'react';
import {Link, useParams} from 'react-router-dom';
import {IUser} from "../models/IUser";
import {UserService} from "../services/UserService";
import Spinner from "../../layout/components/Spinner";

interface IProps {
}

interface IState {
}


let UserDetails: React.FC<IProps> = ({}) => {
    let {userId} = useParams();
    let [loading, setLoading] = useState<boolean>(false);
    let [user, setUser] = useState<IUser>({} as IUser);
    let [errorMessage, setErrorMessage] = useState<string>("");

    useEffect(() => {
        if (userId) {
            setLoading(true);
            UserService.getUser(userId).then((data: any) => {
                setUser(data);
                setLoading(false);
            }).catch((err: { message: React.SetStateAction<string>; }) => {
                setErrorMessage(err.message);
                setLoading(false);
            });
        }
    }, [userId]);

    return (
        <>
            {
                loading && <Spinner/>
            }
            {
                !loading && errorMessage.length > 0 &&
                <div className="row">
                    <div className="col">
                        <p className="h5 text-danger">{errorMessage}</p>
                    </div>
                </div>
            }
            {
                !loading && Object.keys(user).length > 0 &&
                <div className="grid mt-3">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <p className="h3 text-success">{user.name.title}. {user.name.first} {user.name.last}</p>
                                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusantium culpa
                                    dignissimos esse facere iure laborum libero odio perspiciatis rerum sed? Aperiam
                                    autem consequuntur cupiditate doloremque perferendis provident rerum similique
                                    unde.</p>
                            </div>
                        </div>
                        <div className="row mt-3 align-items-center">
                            <div className="col-sm-4 text-center">
                                <img src={user.picture.large} alt="" className="img-fluid shadow-lg"/>
                            </div>
                            <div className="col-sm-8">
                                <ul className="list-group">
                                    <li className="list-group-item">
                                        Name : <span
                                        className="fw-bold">{user.name.title}. {user.name.first} {user.name.last}</span>
                                    </li>
                                    <li className="list-group-item">
                                        Email : <span
                                        className="fw-bold">{user.email}</span>
                                    </li>
                                    <li className="list-group-item">
                                        Phone :<span
                                        className="fw-bold">{user.phone}</span>
                                    </li>
                                    <li className="list-group-item">
                                        Street : <span
                                        className="fw-bold">{user.location.street.name}</span>
                                    </li>
                                    <li className="list-group-item">
                                        City : <span
                                        className="fw-bold">{user.location.city}</span>
                                    </li>
                                    <li className="list-group-item">
                                        State : <span
                                        className="fw-bold">{user.location.state}</span>
                                    </li>

                                </ul>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col">
                                <Link to={'/users'} className="btn btn-success">
                                    <i className="fa fa-arrow-circle-left"></i> Back</Link>
                            </div>
                        </div>
                    </div>
                </div>
            }

        </>
    )
};
export default UserDetails;
