import React, {useEffect} from 'react';
import Spinner from "../layout/Spinner";
import * as userReducer from '../redux/userList/userList.feature';
import {useDispatch, useSelector} from "react-redux";
import {AppDispatch} from "../redux/store";

interface UserListState {
    [userReducer.userListFeature]: userReducer.InitialState
}

let UserList: React.FC = () => {
    const dispatch: AppDispatch = useDispatch();

    // get the data from Redux store
    const userState = useSelector((store: UserListState) => {
        return store[userReducer.userListFeature];
    });

    let {loading, users, errorMessage} = userState;

    useEffect(() => {
        // dispatch  an action
        dispatch(userReducer.getAllUsersFromServer());
    }, []);

    // @ts-ignore
    // @ts-ignore
    return (
        <React.Fragment>
            <div className="grid">
                <div className="container mt-3">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-success">User List</p>
                            <p className="fst-italic">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Atque
                                autem, consequatur culpa, est explicabo facilis fuga, harum incidunt inventore nihil
                                porro quis ratione reprehenderit tempore voluptates? Accusantium delectus porro
                                quae?</p>
                        </div>
                    </div>

                    {
                        loading && <Spinner/>
                    }

                    {
                        errorMessage &&
                        <div className="row">
                            <div className="col">
                                <p className="h3 text-danger text-center">{errorMessage?.message}</p>
                            </div>
                        </div>
                    }

                    {
                        errorMessage && users.length > 0 &&

                        <div className="row">
                            <div className="col">
                                <table className="table table-hover text-center table-sthiped">
                                    <thead>
                                    <tr>
                                        <th>SNO</th>
                                        <th>Name</th>
                                        <th>Email</th>
                                        <th>Street</th>
                                        <th>City</th>
                                        <th>Company</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {
                                        users.map(user => {
                                            return (
                                                <tr key={user.id}>
                                                    <td>{user.id}</td>
                                                    <td>{user.name}</td>
                                                    <td>{user.email}</td>
                                                    <td>{user.address.street}</td>
                                                    <td>{user.address.city}</td>
                                                    <td>{user.company.name}</td>
                                                </tr>
                                            )
                                        })
                                    }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    }
                </div>
            </div>
        </React.Fragment>
    )
};
export default UserList;
