import React, { useState } from "react";
import Users from "./Users";


interface IProps{
    sendData: (someData: string) => void
}
interface IState{}

let Admin:React.FC<IProps> =(sendData) =>{

    let [questions, setQuestions] = useState<string>("");

    let postQuestion =(e: any)=>{
        e.preventDefault();
        let { question} = document.forms[0];
        setQuestions(()=>
                question.value
        )
        sendData(questions);

    }

    return(
        <>
          <div className="grid">
            <div className="container">
                <div className="row">
                    <div className="col">
                    <div className="card">
                        <div className="card-header">
                            <h3>Admin Questions</h3>
                            <form name="questions">
                            <input type="text" name="question" required/>
                            <button onClick={postQuestion} className="btn btn-success mt-4 text-center align-content-center">Post Questions</button>
                            {
                                questions && <Users questions={questions}/>
                            }
                            </form>
                        </div>
                        <div className="card-body">
                            <h3>Users Replay</h3>
                        </div>
                    </div>
                    </div>
                </div>
            </div>
          </div>

        </>
    );

}

export default Admin;
