import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { IProducts } from "../Products/Models/IProducts";
import ProductsService from "../Products/Services/ProductsService";
import AddProduct from "./AddProduct";
import * as productReducer from "../../Redux/ReduxComponent/Product.feature"; 
import { useSelector } from "react-redux";
import Store, { AppDispatch } from "../../Redux/Store";
import { useDispatch } from "react-redux";
import { type } from "@testing-library/user-event/dist/type";
import Spinner from "../../Layouts/Spinner";
import * as productAction from "../../Redux/ReduxComponent/Product.actions"; 
interface IProps{}
interface IProductState{
    [productReducer.productFeature]:productReducer.InitialState;
}

let ProductList:React.FC<IProps> =() =>{


    let Products = useSelector((Store:IProductState)=>{
        return Store[productReducer.productFeature];
    });
    let dispatch:AppDispatch = useDispatch();
    let {loading,errorMessage,product} = Products;

    useEffect(()=>{
        dispatch(productAction.getAllProductsFromServer());
    },[])


    // let [product , setProduct] = useState<IProducts[]>([] as IProducts[]);
    // let [loading , setLoading] = useState<boolean>(false);
    // let [errorMessage ,setErrorMessage] = useState<string>("");
    // useEffect(()=>{
    //         setLoading(true);
    //     ProductsService.getAllProducts().then((response)=>{
    //         setProduct(()=>response.data);
    //         setLoading(false);
    //     }).catch((err)=>{
    //         setErrorMessage(()=>err.message);
    //         setLoading(false);
    //     })
    // },[])

    let updateProduct=(sno:number)=>{

    }

    let deleteProduct=(productId:string|undefined)=>{
        // if(productId){
        //     dispatch(productReducer.deleteProductFromServer(productId));
        // }
        let response = ProductsService.deleteProduct(productId);
        console.log(response);
    }

    return(
        <>
            <div className="grid">
                <div className="container">
                    <div className="row m-3">
                        <div className="col">
                            <Link className="btn btn-group-vertical btn-danger" to={"/admin/add-product"}>Create Product</Link>
                        </div>
                    </div>
                    <div className="row m-3">
                        <div className="col">
                            <div className="">
                                <div className="">
                                    {
                                        loading && <Spinner/>
                                    }
                                    {
                                        !loading && errorMessage && 
                                        <div className="row">
                                            <p className="text-danger display-6">{errorMessage.message}</p>
                                        </div>
                                    }
                                    {
                                        !loading && !errorMessage.message && product.length === 0 &&
                                        <div className="row">
                                            <p className="text-warning display-6">No Data Found</p>
                                        </div>
                                    }
                                    {
                                        !loading && product && product.length > 0 && 
                                        <table className="table table-hover table-bordered text-center">
                                            <thead>
                                                <tr className="table-dark">
                                                <th>S No</th>
                                                <th>Product</th>
                                                <th>Name</th>
                                                <th>Price</th>
                                                <th>Qty</th>
                                                <th>Actions</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {
                                                    product.map((products)=>{
                                                        return(
                                                            <tr key={products.id}>
                                                                <td>{products.id}</td>
                                                                <td><img height={100} width={100} src={products.imageUrl} alt="No Image" /></td>
                                                                <td>{products.name}</td>
                                                                <td>{products.qty}</td>
                                                                <td> &#8377; {parseInt(products.price).toFixed(2)}</td>
                                                                <td>
                                                                <Link className="btn btn-primary" to={`/admin/update-product/${products.id}`} >Update</Link>
                                                                <button onClick={()=>deleteProduct(products.id)} className="btn btn-warning m-2">Delete</button>
                                                                </td>
                                                            </tr>
                                                        )
                                                    })
                                                }
                                            </tbody>
                                        </table>
                                    }

                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );

}

export default ProductList;
