import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import Store, { AppDispatch } from "../../Redux/Store";
import * as productReducer from "../../Redux/ReduxComponent/Product.feature";
import * as productAction from "../../Redux/ReduxComponent/Product.actions";
import { productFeature } from "../../Redux/ReduxComponent/Product.feature";

interface IProps{}
interface IProductState{
    [productReducer.productFeature]:productReducer.InitialState;
}

let UpdateProduct:React.FC<IProps> =() =>{

     let [product, setProduct] = useState({
        name:"",
        price:0,
        info:"",
        imageUrl:"",
        qty:0
    })

    let {productId} = useParams();
    const dispatch:AppDispatch= useDispatch();
    const navigate = useNavigate();

    let products = useSelector((Store:IProductState)=>{
        return Store[productReducer.productFeature];
    });

    const {loading, errorMessage, product: reduxProduct } = products;

    const updateInput = (event:React.ChangeEvent<HTMLInputElement|HTMLSelectElement|HTMLTextAreaElement>)=>{
        setProduct((prevState)=>{
            return{
                ...prevState,
                [event.target.name]:event.target.value
            }
        })
    }

    useEffect(()=>{
        if(productId){
            dispatch(productAction.getProductFromServer(productId));
        }
    },[productId])

    useEffect(() => {
        if (Object.keys(reduxProduct).length > 0) {
            setProduct((prevState) => {
                return {
                    ...prevState,
                    name: reduxProduct.name ? reduxProduct.name : "",
                    imageUrl: reduxProduct.imageUrl ? reduxProduct.imageUrl : "",
                    mobile: reduxProduct.mobile ? reduxProduct.mobile : "",
                    email: reduxProduct.email ? reduxProduct.email : "",
                    company: reduxProduct.company ? reduxProduct.company : "",
                    title: reduxProduct.title ? reduxProduct.title : "",
                    groupId: reduxProduct.groupId ? reduxProduct.groupId : "",
                }
            })
        }
    }, [reduxContact])

    return(
        <>
            <div className="grid">
                <div className="container">
                    <div className="row mt-2">
                        <div className="col">
                            <p className="display-6">Update Product</p>
                            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Explicabo, culpa iste esse animi suscipit earum soluta doloremque, quaerat vitae magni repellat mollitia distinctio quasi! Mollitia laudantium distinctio possimus natus laborum?</p>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-5">
                            <div className="card shadow-lg">
                                <div className="card-header">
                                    <h3>New Product</h3>
                                </div>
                                <div className="card-body">
                                <form>
                                    <div className="form-group">
                                        <input type="text" className="form-control" id="product-name" placeholder="Product Name"/>
                                    </div>
                                    <div className="form-group mt-2">
                                        <input type="text" className="form-control" id="product-image" placeholder="Product Image Url"/>
                                    </div>
                                    <div className="form-group mt-2">
                                        <input type="text" className="form-control" id="price" placeholder="Price"/>
                                    </div>
                                    <div className="form-group mt-2">
                                        <input type="text" className="form-control" id="qty" placeholder="Available Qty"/>
                                    </div>
                                    <div className="form-group mt-2">
                                        <textarea className="form-control" name="info" id="info" cols={3} placeholder="General Info"></textarea>
                                    </div>
                                    <button type="submit" className="btn btn-primary mt-2">UPDATE</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );

}

export default UpdateProduct;
