import React, {useState} from 'react';
interface IProps {
}

interface IState {
    count: number;
}

let Counter: React.FC<IProps> = ({}) => {
    let [state, setState] = useState<IState>({
        count: 0
    });

    let {counter} = count;

    let clickIncr = (): void => {
        setState({
            counter: state.counter + 1
        })
    };

    let clickDecr = (): void => {
        setState({
            counter: state.counter - 1 > 0 ? state.counter-1 : 0
        })
    };

    let clickIncrBy = (value: number): void => {
        setState({
            counter: state.counter + value
        });
    };

    return (
        <React.Fragment>
            <div className="container mt-5">
                <div className="row">
                    <div className="col-md-4">
                        <div className="card">
                            <div className="card-body">
                            <p className="h5">Without Redux State Changing</p>
                                <p>Reduct Counter State : {counter}</p>
                                <p className="h3 display-3">{state.counter}</p>
                                <button onClick={clickIncr} className="btn btn-success m-1">Increment</button>
                                <button onClick={clickDecr} className="btn btn-warning m-1">Decrement</button>
                                <button onClick={() => clickIncrBy(5)} className="btn btn-danger m-1">Increment by 5
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </React.Fragment>
    )
};
export default Counter;
