import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { IEmployee } from "../models/IEmployee";
import * as employeeReducer from "../Redux/Employee/employee.feature";
import { employeeFeature } from "../Redux/Employee/employee.feature";
import Store, { AppDispatch } from "../Redux/Store";
import EmployeeService from "../services/EmployeeService";


interface IProps{}

interface EmplyeeState {
    [employeeReducer.employeeFeature]:employeeReducer.InitialState;
}

let EmployeeRedux:React.FC<IProps> =() =>{

    // let [state, setState] = useState<IState>({
    //     employees:EmployeeService.getAllEmployee()
    // });

    let dispatch:AppDispatch=useDispatch();

    let employees = useSelector((Store:EmplyeeState)=>{
        return Store[employeeFeature]
    })

    let {employee} = employees;
    let dispalyRecords=(id:number)=>{
        dispatch({
            type:`${employeeReducer.dispalyRecords}`,
            payload:{id}
        })
    }
    return(
        <>
        <div className="grid">
            <div className="container">
                <div className="row mt-5">
                    <div className="col-sm-6">
                        <ul className="list-group">
                            {
                                employee.map((employee)=>{
                                    return(
                                        <li className="list-group-item" key={employee.id}>
                                            <input checked={employee.isSelected} onChange={()=>dispalyRecords(employee.id)} type="checkbox" className="form-check-input me-2" />
                                            {employee.name}
                                        </li>
                                    )
                                })
                            }
                        </ul>
                    </div>
                    <div className="col-sm-6">
                        {
                            employee.map((emp)=>{
                                return(
                                    <div key={emp.id}>
                                        {
                                            emp.isSelected &&
                                            <div className="card mt-2 shadow-lg">
                                                <div className="card-body">
                                                    <ul className="list-group">
                                                        <li className="list-group-item">
                                                            {emp.name}
                                                        </li>
                                                        <li className="list-group-item">
                                                            {emp.email}
                                                        </li>
                                                    </ul>
                                                </div>
                                            </div>
                                        }
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </div>
        </>
    );

}

export default EmployeeRedux;
