import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import { IUsers } from "../Models/IUSers";
import { UserService } from "../Services/UserService";

interface IProps{}

interface IState{}

let UserDetails:React.FC<IProps>=()=>{

    // let {userId} = useParams();
    // let [user, setUser] = useState<IUsers>({} as IUsers);
    // let [errorMessage, setErrorMessage] = useState<string>("");
    // let [loading, setLoading] = useState<boolean>(false);

    let {userId} = useParams();
    let [loading, setLoading] = useState<boolean>(false);
    let [user, setUser] = useState<IUsers>({} as IUsers);
    let [errorMessage, setErrorMessage] = useState<string>("");

    useEffect(()=>{
        if(userId){
            setLoading(()=>true);
            UserService.getUser(userId).then((data:any)=>{
                setUser(data);
                setLoading(()=>false);
            }).catch((err)=>{
                setErrorMessage(()=>err.message);
                setLoading(()=>false);
            })
        }
    },[userId])

    return(
        <>
        <div className="grid">
            <div className="conatiner">
                <div className="row m-5">
                    <div className="col">
                        <h3></h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Tenetur deserunt impedit, aut incidunt iure fugiat modi excepturi numquam. Doloribus aut tempora illo harum aliquam eius ullam labore modi laudantium eaque?</p>                        
                    </div>
                </div>
                <div className="row m-5">
                    <div className="col">
                        <div className="card">
                            <div className="card-body">
                                <div className="row">
                                    <div className="col-sm-4 text-center">
                                        <img src={user.picture.large} alt="" />
                                    </div>
                                    <div className="col-sm-6">
                                        <ul className="list-group">
                                            <li className="list-group-item">Name : </li>
                                            <li className="list-group-item">Email : {user.email}</li>
                                            <li className="list-group-item">City : {user.location.city}</li>
                                            <li className="list-group-item">Country : {user.location.country}</li>
                                            <li className="list-group-item">Phone : {user.phone}</li>
                                        </ul> 
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <Link to={"/users"} className="btn btn-success mx-5"><i className="fa fa-arrow-circle-left"></i> Back</Link>
            </div>
        </div>
        </>
    )
}

export default UserDetails;
