import React, {useEffect, useState} from 'react';
import MainHeader from "../../../layout/components/main-Header/MainHeader";
import {Link, useParams, useNavigate} from "react-router-dom";
import * as contactFeature from '../../../../redux/contacts/contacts.slice';
import * as contactActions from '../../../../redux/contacts/contacts.actions';
import {useDispatch, useSelector} from "react-redux";
import {AppDispatch} from "../../../../redux/store";
import LoadingSpinner from "../../../util/components/spinner/LoadingSpinner";
import ErrorMessage from "../../../util/components/error-message/ErrorMessage";
import {IContact} from "../../models/IContact";

interface IProps {
}

interface IState {
}

let EditContact: React.FC<IProps> = ({}) => {

    let [contact, setContact] = useState<IContact>({
        name: "",
        imageUrl: "",
        mobile: "",
        email: "",
        company: "",
        title: "",
        groupId: ""
    });

    let {contactId} = useParams();
    const dispatch: AppDispatch = useDispatch();
    const navigate = useNavigate();

    // get the store data from redux
    let contactState = useSelector((store: contactFeature.RootContactState) => {
        return store[contactFeature.contactsFeatureKey];
    });

    const {loading, errorMessage, contact: reduxContact, groups} = contactState;

    const updateInput = (event: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        setContact((prevState) => {
            return {
                ...prevState,
                [event.target.name]: event.target.value
            }
        })
    };

    let isEmptyContact = () => {
        for (let value of Object.values(contact)) {
            if (value === "") {
                return true;
            }
        }
        return false;
    };

    useEffect(() => {
        if (contactId) {
            dispatch(contactActions.getContactAction(contactId));
            dispatch(contactActions.getAllGroupsAction());
        }

    }, [contactId])

    useEffect(() => {
        if (Object.keys(reduxContact).length > 0) {
            setContact((prevState) => {
                return {
                    ...prevState,
                    name: reduxContact.name ? reduxContact.name : "",
                    imageUrl: reduxContact.imageUrl ? reduxContact.imageUrl : "",
                    mobile: reduxContact.mobile ? reduxContact.mobile : "",
                    email: reduxContact.email ? reduxContact.email : "",
                    company: reduxContact.company ? reduxContact.company : "",
                    title: reduxContact.title ? reduxContact.title : "",
                    groupId: reduxContact.groupId ? reduxContact.groupId : "",
                }
            })
        }
    }, [reduxContact])

    const submitUpdate = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        if (contactId) {
            dispatch(contactActions.updateContactAction({contact: contact, contactId: contactId})).then(() => {
                navigate('/contacts/admin');
            });
        }
    };

    return (
        <>
            <MainHeader heading={'Update Contact'}/>

            {/* Loading LoadingSpinner */}
            {
                loading && Object.keys(contact).length === 0 &&
                <LoadingSpinner/>
            }

            {/* Error Message */}
            {
                !loading && errorMessage.message && errorMessage.message?.length > 0 &&
                <ErrorMessage message={errorMessage.message}/>
            }

            {
                !loading && Object.keys(contact).length > 0 &&
                <div className="container">
                    <div className="row">
                        <div className="col-sm-4">
                            <form onSubmit={submitUpdate}>
                                <div className="mb-2">
                                    <input
                                        name={'name'}
                                        onChange={updateInput}
                                        value={contact.name}
                                        type="text" className="form-control" placeholder={'Name'}/>
                                </div>
                                <div className="mb-2">
                                    <input
                                        name={'imageUrl'}
                                        onChange={updateInput}
                                        value={contact.imageUrl}
                                        type="text" className="form-control" placeholder={'Image Url'}/>
                                </div>
                                <div className="mb-2">
                                    <input
                                        name={'mobile'}
                                        onChange={updateInput}
                                        value={contact.mobile}
                                        type="number" className="form-control" placeholder={'Mobile'}/>
                                </div>
                                <div className="mb-2">
                                    <input
                                        name={'email'}
                                        onChange={updateInput}
                                        value={contact.email}
                                        type="email" className="form-control" placeholder={'Email'}/>
                                </div>
                                <div className="mb-2">
                                    <input
                                        name={'company'}
                                        onChange={updateInput}
                                        value={contact.company}
                                        type="text" className="form-control" placeholder={'Company'}/>
                                </div>
                                <div className="mb-2">
                                    <input
                                        name={'title'}
                                        onChange={updateInput}
                                        value={contact.title}
                                        type="text" className="form-control" placeholder={'Title'}/>
                                </div>
                                <div className="mb-2">
                                    <select
                                        name={'groupId'}
                                        onChange={updateInput}
                                        value={contact.groupId}
                                        className="form-control">
                                        <option value="">Select Group..</option>
                                        {
                                            groups.length > 0 && groups.map(group => {
                                                return <option key={group.id} value={group.id}>{group.name}</option>
                                            })
                                        }
                                    </select>
                                </div>
                                <div className="mb-2">
                                    <input disabled={isEmptyContact()} type="submit" className="btn btn-success me-3"
                                           value={'Update'}/>
                                    <Link to={'/contacts/admin'} className="btn btn-outline-dark">Cancel</Link>
                                </div>

                            </form>
                        </div>
                        <div className="col-sm-3">
                            <img src={contact.imageUrl} alt="" className="img-fluid rounded-circle"/>
                        </div>
                    </div>
                </div>
            }
        </>
    )
};
export default EditContact;
