import React, { useRef, useState } from "react";
import Users from "./Users";


interface IProps{
    sendAdminData: (someData: string) => void,
    reply:string;
}
interface IState{}

let Admin:React.FC<IProps> =({sendAdminData,reply}) =>{

    let [questions, setQuestions] = useState<string>("");
    let questionRef:any = useRef();
    let postQuestion =(e: any)=>{
        e.preventDefault();
        // let { question} = document.forms[0];
        // sendAdminData(question.value);
        // question.value = "";
        sendAdminData(questionRef.current.value);
        questionRef.current.value = "";
    }

    return(
        <>
          <div className="grid">
            <div className="container">
                <div className="row">
                    <div className="col">
                    <div className="card">
                        <div className="card-header">
                            <h3>Admin Questions</h3>
                            <form name="questions">
                            <input type="text" ref={questionRef} name="question" required/>
                            <button onClick={postQuestion} className="btn btn-success mt-4 text-center align-content-center">Post Questions</button>
                            </form>
                        </div>
                        <div className="card-body">
                            <h3>Users Replay</h3>
                            <p>{reply}</p>
                        </div>
                    </div>
                    </div>
                </div>
            </div>
          </div>

        </>
    );

}

export default Admin;
