import {createSlice} from "@reduxjs/toolkit";

export const counterFeature = "counterFeature";

export interface InitialState {
    count: number;
}

const initialState: InitialState = {
    count: 0
}

export const counterSlice = createSlice({
    name: 'counterSlice',
    initialState: initialState,
    reducers: {
        clickIncr: (state, action) => {
            state.count = state.count + 1
        },
        clickDecr: (state, action) => {
            state.count = state.count - 1 > 0 ? state.count - 1 : 0
        },
        clickIncrBy: (state, action) => {
            let {value} = action.payload;
            state.count = state.count + value
        },
    }
});
export const {clickIncr, clickDecr, clickIncrBy} = counterSlice.actions;
